function display(e){e<10&&(e="0"+e),$(".display").text(e)}function timer(e){var t=setInterval(function(){e--,deg+=angle,display(e),e>=mid?$(".mask-right").css("transform","rotate("+deg+"deg)"):$(".mask-left").css("transform","rotate("+deg+"deg)"),e===mid?(deg=0,$(".mask-right").css("z-index","2"),$(".mask-left").removeClass("handle")):0===e&&(clearInterval(t),$(".display").css("color","#008c9e"))},1e3)}var deg=0,count=60,mid=count/2,angle=180/mid;timer(count);